import { stat } from "fs";
import React from "react";
import { useActions } from "../../hooks/useActions";
import { useTypedSelector } from "../../hooks/useTypedSelector";
import "./CheckoutOrder.css";
import ModalPage from "./ModalPage/ModalPage";

const CheckoutOrder = () => {
  const { orderItem, orderPrice, orderCurrency, orderResult } = useTypedSelector((state) => state.order);
  const {
    decreaseOrderItemAction,
    increaseOrderItemAction,
    removeFromOrderAction,
    fetchOrder,
    clearOrderAction,
  } = useActions();
  const countItems: any = {};
  console.log(orderItem);

  const orderItemCount: any = {};
  orderItem.forEach((item) => {
    orderItemCount[item.id] = (orderItemCount[item.id] || 0) + 1;
  });

  // Создаем новый массив, в котором каждый элемент массива orderItem представлен только один раз
  const uniqueOrderItem = orderItem.filter(
    (item, index, self) => self.findIndex((t) => t.id === item.id) === index
  );

  const clearOrder = () => {
    orderItem.length > 0 
    ? clearOrderAction () 
    : alert('Заказ и так пустой')
  }

  const chekoutOrder = () => {
    orderItem.length > 0 
      ? fetchOrder(orderItem)
      : alert('Не выбрано ни одной позиции')
  }

  return (
    <div className="orderPage">
      <div className="orderContainer">
        <div className="orderMenu">
          <div>
          <h3>
            Корзина / {orderItem.length} &nbsp;
            <img
             onClick={() =>  clearOrder ()}
              className="clearOrderButton" 
              src="https://img.icons8.com/ios/512/cancel.png"
              width={"25px"}
            />
          </h3>
          <h4> Сумма: {orderPrice} {orderCurrency} </h4>
          </div>
          <div className="orderCheckOrClearContainer"> 
            <button 
            onClick={() =>  clearOrder ()}
            className="checkoutOrder">
              Отчистить
              <img
                src="https://img.icons8.com/ios/512/shopping-cart--v1.png"
                width={"25px"}
              />
            </button>
            <button 
            onClick={ () => chekoutOrder()}
            className="clearOrder">
              Оформить
              <img
                src="https://img.icons8.com/ios/512/checkout.png"
                width={"25px"}
              />{" "}
            </button>
          </div>

        </div>
        <div className="orderList">
          <h3> Товары</h3>
          <div className="orderItemContainer">
            {uniqueOrderItem.map((item) => (
              <div className="orderElement" key={item.id}>
                <img src={item.image} width={"50px"} />
                <div className="orderInform">
                  <p>
                    {item.title} ({orderItemCount[item.id]} шт.){" "}
                  </p>
                  <button onClick={() => decreaseOrderItemAction(item)} className="deleteItemButton"/> 
                  <button onClick={() => increaseOrderItemAction(item)} className="addOneItemButton" /> 
                </div>

                <button onClick={() => removeFromOrderAction(item.id)} className="clearItemButton" /> 
              </div>
            ))}
          </div>
        </div>
      </div>
      <ModalPage />
    </div>
  );
};

export default CheckoutOrder;
