import { stat } from "fs";
import React from "react";
import { useActions } from "../../hooks/useActions";
import { useTypedSelector } from "../../hooks/useTypedSelector";
import "./CheckoutOrder.css";

const CheckoutOrder = () => {
  const { orderItem } = useTypedSelector((state) => state.order);
  const {
    decreaseOrderItemAction,
    increaseOrderItemAction,
    removeFromOrderAction,
  } = useActions();
  const countItems: any = {};

  const orderItemCount: any = {};
  orderItem.forEach((item) => {
    orderItemCount[item.id] = (orderItemCount[item.id] || 0) + 1;
  });

  // Создаем новый массив, в котором каждый элемент массива orderItem представлен только один раз
  const uniqueOrderItem = orderItem.filter(
    (item, index, self) => self.findIndex((t) => t.id === item.id) === index
  );

  return (
    <div className="orderPage">
      <div className="orderContainer">
        <div className="orderMenu">
          <h3>
            {" "}
            Корзина / {orderItem.length} &nbsp;{" "}
            <img
              src="https://img.icons8.com/ios/512/cancel.png"
              width={"25px"}
            />{" "}
          </h3>
          <button className="checkoutOrder">
            Отчистить
            <img
              src="https://img.icons8.com/ios/512/shopping-cart--v1.png"
              width={"25px"}
            />
          </button>
          <button className="clearOrder">
            Оформить
            <img
              src="https://img.icons8.com/ios/512/checkout.png"
              width={"25px"}
            />{" "}
          </button>
        </div>
        <div className="orderList">
          <h3> Товары</h3>
          <div>
            {uniqueOrderItem.map((item) => (
              <div className="orderElement" key={item.id}>
                <img src={item.image} width={"50px"} />
                <div>
                  <p>
                    {item.title} (Количество: {orderItemCount[item.id]} шт.){" "}
                  </p>
                </div>
                <button onClick={() => removeFromOrderAction(item.id)}>
                  Удалить
                </button>
                <button onClick={() => decreaseOrderItemAction(item)}>-</button>
                <button onClick={() => increaseOrderItemAction(item)}>+</button>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default CheckoutOrder;
