    allProducts: [],

    displayProducts: [],

    filterByBrand: [],

    displayProductsWithFilter: [],
    displayProductsWithoutFilter: [],

    startDisplayInd: 0,
    countDisplayProduct: 6,
    pageProduct: 1,
}

export const productsReducer = (state = initialState, action: ProductsActions ):ProductState => {
    switch(action.type) {
        case ProductsActionTypes.GET_ALL_PRODUCT: {
            return {
                ...state,
