import { OrderActionTypes } from "../../types/order";
import { Product } from "../../types/products";

export const addToOrderAction = (item : Product) => {
    return {type: OrderActionTypes.ADD_TO_ORDER, payload: item}
}

export const decreaseOrderItem = (item: any) => {
    return { type: 'DECREASE_ORDER_ITEM', payload: item };
  };

  export const increaseOrderItem = (item) => {
    return { type: 'INCREASE_ORDER_ITEM', payload: item };
  };

  export const removeFromOrder = (item) => {
    return { type: 'REMOVE_FROM_ORDER', payload: item };
  };
