import React from "react";
import ProductElement from "./ProductElement/ProductElement";
import './ProductContainer.css'
import { useTypedSelector } from "../../hooks/useTypedSelector";
import { Product } from "../../types/products";

const ProductContainer = () => {
    const {allProducts, brandsFilter, viewProducts} = useTypedSelector(state => state.product)
    console.log(viewProducts)

    const displayProducts:Product[] = []
     allProducts.map ( el => {
        brandsFilter.map( bra => {
            if( bra === el.brand ) {
                displayProducts.push(el) 
                viewProducts.push(el)
            }
        })
    })

    if (displayProducts.length > 0) return (
        <div className="ProductContainer">
            {displayProducts.map( el => (
                <ProductElement key={el.id} product={el} />
            ))}
        <button className="loadMoreButton" style={{alignContent: 'center'}}> load more items </button>
        </div>
    )

    for (let i = 0; i < 6; i++) {
        allProducts.map( el => {
            for( let i = 0; i < 6 ; i++) {
               return  <ProductElement key={el.id} product={el} />
            }
        })
    }

    return (
        <div className="ProductContainer">
            {allProducts.map( el => {
                for( let i = 0; i < 6 ; i++) {
                   return  <ProductElement key={el.id} product={el} />
                }
            })}
             <button className="loadMoreButton" style={{alignContent: 'center'}}> load more items </button>
        </div>
    )
}

export default ProductContainer
