import { useTypedSelector } from './../../hooks/useTypedSelector';
import { ProductsActionTypes } from './../../types/products';

const {} = useTypedSelector(state => state.product)

export const getProductAction = (products: any[]) => {
    return {type: ProductsActionTypes.GET_ALL_PRODUCT, payload: products}
}

export const setBrandsFilterAction = (brandNumber: number) => {
    return {type: ProductsActionTypes.SET_BRANDS_FILTER, payload: brandNumber}
}
