import { ProductState, ProductsActions, ProductsActionTypes } from './../../types/products';

let initialState: ProductState = {
    allProducts: [],
    displayProducts: [],
    filterByBrand: [],

    startDisplayInd: 0,
    ve: 6,
    pageProduct: 1,
}

export const productsReducer = (state = initialState, action: ProductsActions ):ProductState => {
    switch(action.type) {
        case ProductsActionTypes.GET_ALL_PRODUCT: {
            return {
                ...state,
                allProducts: [...action.payload],
                displayProducts: [ ...action.payload.splice(state.startDisplayInd + 6, state.endDisplayInd)],
            }
        }
        case ProductsActionTypes.SET_BRANDS_FILTER:
            if (state.filterByBrand.length === 0) return {                   //добавление певого бренда
                ...state,
                filterByBrand: [ action.payload]
            }
            if (state.filterByBrand.find ( (el) => el === action.payload )) {    // проверка на наличие этого бренда
                const newBrandsFilter = state.filterByBrand.filter( el => el !== action.payload)
                return {
                    ...state,
                    filterByBrand: [...newBrandsFilter]
                }
            } 
            return {                                                            // добавление последующих брендов
                ...state, 
                filterByBrand: [...state.filterByBrand, action.payload]
            }
        case ProductsActionTypes.VIEW_PRODUCTS: 
            return {
                ...state,
                displayProducts:[...state.allProducts.splice(0, action.payload)]
            }

        case ProductsActionTypes.GO_TO_NEXT_PAGE: 
            return {
                ...state,
                displayProducts: state.allProducts.splice(state.startDisplayInd + 6, state.endDisplayInd + 6),
                startDisplayInd: state.startDisplayInd + 6,
                endDisplayInd: state.endDisplayInd + 6,
                pageProduct: state.pageProduct + 1
            }
        case ProductsActionTypes.GO_TO_PREVIOUS_PAGE:
            return {
                ...state,
                displayProducts: [...state.allProducts.splice(state.startDisplayInd -6, state.endDisplayInd -6)],
                startDisplayInd: state.startDisplayInd -6,
                endDisplayInd: state.endDisplayInd -6,
                pageProduct: state.pageProduct - 1
            }

        default: 
            return {
                ...state,
            }
    }
}
