import { OrderState, OrderActionTypes, OrderActions } from './../../types/order';


const initialState: OrderState = {
    orderItem: [],
    orderPrice: 0,
    orderCurrency: "USD",
}

export const orderReducer = (state = initialState, action: OrderActions): OrderState => {
    switch(action.type) {
        case OrderActionTypes.ADD_TO_ORDER: 
        const itemPrice:any = action.payload
            return {
                ...state,
                orderItem: [...state.orderItem, action.payload],
                orderPrice: state.orderPrice + itemPrice.regular_price.value
            }
              case  OrderActionTypes.REMOVE_FROM_ORDER:

                return { 
                    ...state, 
                    orderItem: state.orderItem.filter((item) => item.id !== action.payload) 
                };
              case OrderActionTypes.DECREASE_ORDER_ITEM:

                // const orderItemIndex = state.orderItem.indexOf(action.payload)

                // console.log(orderItemIndex);
                // console.log(state.orderItem);
                // const news = state.orderItem.splice(1, 1)

                const deletedItem = state.orderItem.findIndex( el => el.id === action.payload.id)
                console.log(deletedItem);

                return { 
                    ...state, 
                    // orderItem: [...state.orderItem.splice(deletedItem , 1 )]
                };

              case OrderActionTypes.INCREASE_ORDER_ITEM:

                return { 
                    ...state, 
                    orderItem: [...state.orderItem, action.payload]
                };

        default: 
        return {
            ...state
        }
    }
}
