import React, { useEffect, useState } from "react";
import { useActions } from "../../../hooks/useActions";
import { useTypedSelector } from "../../../hooks/useTypedSelector";
import { BrandsState } from "../../../types/brand";
import "./BrandsFilter.css";

const BrandsFilter = () => {
  const { brands, viewBrands } = useTypedSelector((state) => state.brand);

  const { setBrandsFilterAction } = useActions();

  function onChangeBrandsChecked (e: React.ChangeEvent<HTMLInputElement>) {
    setBrandsFilterAction(Number(e.target.value))
  }

  return (
    <>

      <div className="filterBrands" style={{ display: viewBrands === false ? 'none' : "block"}}>
        {brands.map((el) => (
          <div key={el.id} className="brandsList">
            <input type="checkbox" checked={ () => lo} value={el.id} onChange={onChangeBrandsChecked} />
            <p> {el.title} </p>
          </div>
        ))}
      </div>
    </>
  );
};

export default BrandsFilter;
