import { ProductState, ProductsActions, ProductsActionTypes } from './../../types/products';

let initialState: ProductState = {
    allProducts: [],
    viewProducts: [],
    brandsFilter: [],
}

export const productsReducer = (state = initialState, action: ProductsActions ):ProductState => {
    switch(action.type) {
        case ProductsActionTypes.GET_ALL_PRODUCT: {
            return {
                ...state,
                allProducts: action.payload,
            }
        }
        case ProductsActionTypes.SET_BRANDS_FILTER:
            if (state.brandsFilter.length === 0) return {                   //добавление певого бренда
                ...state,
                brandsFilter: [ action.payload]
            }
            if (state.brandsFilter.find ( (el) => el === action.payload )) {    // проверка на наличие этого бренда
                const newBrandsFilter = state.brandsFilter.filter( el => el !== action.payload)
                return {
                    ...state,
                    brandsFilter: [...newBrandsFilter]
                }
            } 
            return {                                                            // добавление последующих брендов
                ...state, 
                brandsFilter: [...state.brandsFilter, action.payload]
            }
        case ProductsActionTypes.VIEW_PRODUCTS: 

            if (state.brandsFilter.length === 0) {
                return {
                    ...state,
                    viewProducts: [...state.allProducts]
                }
            }

        default: 
            return {
                ...state,
            }
    }
}
