import React, { useEffect } from "react";
import ProductElement from "./ProductElement/ProductElement";
import './ProductContainer.css'
import { useTypedSelector } from "../../hooks/useTypedSelector";
import { Product } from "../../types/products";
import { useActions } from "../../hooks/useActions";

const ProductContainer = () => {
    const {
        getAllProducts, 
        filterByBrand, 
        displayProducts, 
        pageProduct,
        brands_filter, 
        productWithFilter } = useTypedSelector(state => state.product)
    const {viewProducts, goToNextPageAction, goToPreviousPageAction} = useActions()
    let brandsFilter: []
    console.log(brands_filter);

    const display:Product[] = []
    getAllProducts.map ( el => {
        filterByBrand.map( bra => {
            if( bra === el.brand ) {
                display.push(el) 
            }
        })
    })

    // function displayProductSS () {
    //     const disp = []
    //     getAllProducts.map ( el => {
    //         brandsFilter.map ( b => {
    //             disp.push(el => el)
    //         })
    //     })
    // }

    const viewWithFilter = displayProducts.map( el => <ProductElement key={el.id} product={el} />)
    const viewWithoutFilter = displayProducts.map( el => <ProductElement key={el.id} product={el} />)

    function nextPage () {
        goToNextPageAction()
    }

    function previousPage () {
        goToPreviousPageAction()
    }

    return (
        <div className="ProductContainer">
            { filterByBrand.length > 0 
                ? 
                viewWithFilter 
                : 
                viewWithoutFilter 
            }
            <div className="changePages">
                <button 
                onClick={ () => previousPage()}
                className="loadMoreButton" 
                disabled={ pageProduct === 1 ? true : false} 
                style={{alignContent: 'center'}}> 
                    Previous page 
                </button>

                <p> {pageProduct} </p>
                <button 
                className="loadMoreButton" 
                onClick={ () => nextPage()}
                style={{alignContent: 'center'}}> 
                    Next page 
                </button>
            </div>

        </div>
    )
}

export default ProductContainer
