export interface ProductState {
    allProducts: any[],
    displayProductWithFilter: any[],
    filterByBrand: number[],
}

export interface Product {
    type: string,
    id: number,
    sku: string,
    title: string,
    regular_price: {
      currency: string,
      value: number
    },
    image: string,
    brand: number,
}

export enum ProductsActionTypes{
    GET_ALL_PRODUCT = "GET_ALL_PRODUCT",
    VIEW_PRODUCTS = "VIEW_PRODUCTS",

    SET_BRANDS_FILTER = "SET_BRANDS_FILTER",

    ADD_TO_ORDER = "ADD_TO_ORDER",
    DELETE_FROM_ORDER = "DELETE_FROM_ORDER",
    COMPLETE_ORDER = "COMPLETE_ORDER",
}

interface getProductAction {
    type: ProductsActionTypes.GET_ALL_PRODUCT,
    payload: any[],
}

interface viewProductsAction {
    type: ProductsActionTypes.VIEW_PRODUCTS,
}

interface setBrandsFilterAction {
    type: ProductsActionTypes.SET_BRANDS_FILTER,
    payload: number,
}

interface addToArdersAction {
    type: ProductsActionTypes.ADD_TO_ORDER,
}


export type ProductsActions = 
getProductAction |
viewProductsAction |
setBrandsFilterAction |
addToArdersAction |
deleteFromOrderAction |
completeOrderAction
