import { useTypedSelector } from './../../hooks/useTypedSelector';
import { ProductsActionTypes, ProductState } from './../../types/products';


export const getProductAction = (products: ProductState) => {
    return {type: ProductsActionTypes.GET_ALL_PRODUCT, payload: products}
}

export const addBrandToFilterAction = (brand: number) => {
    return {type: ProductsActionTypes.ADD_BRAND_TO_FILTER, payload: num}
}






export const viewProducts = (num: number) => {
    return {type: ProductsActionTypes.VIEW_PRODUCTS, payload: num}
}




export const goToNextPageAction = () => {
    return {type: ProductsActionTypes.GO_TO_NEXT_PAGE}
}

export const goToPreviousPageAction = () => {
    return {type: ProductsActionTypes.GO_TO_PREVIOUS_PAGE}
}

export const setBrandsFilterAction = (brandNumber: number) => {
    return {type: ProductsActionTypes.SET_BRANDS_FILTER, payload: brandNumber}
}

export const setViewProductAction = (product: ProductState) => {
    return {type: ProductsActionTypes.VIEW_PRODUCTS, payload: product}
}
