import { ProductState, Product, ProductsActions, ProductsActionTypes } from './../../types/products';

let initialState: ProductState = {
    getProducts: [],

    productWithFilter: [],
    productWithoutFilter: [],

    displayProducts: [],

    filterByBrand: [],

    startDisplayInd: 0,
    countDisplayProduct: 6,
    pageProduct: 1,
}

export const productsReducer = (state = initialState, action: ProductsActions): ProductState => {
    switch (action.type) {
        case ProductsActionTypes.GET_ALL_PRODUCT: {
            return {
                ...state,
                getProducts: [...action.payload],
                // productWithoutFilter: [...action.payload],
                displayProducts: [...action.payload.splice(state.startDisplayInd, state.countDisplayProduct)],
            }
        }
        case ProductsActionTypes.SET_BRANDS_FILTER:
            if (state.filterByBrand.length === 0) { 
                const newPro: Product[] = state.getProducts.filter(el => el.brand === action.payload)
                return {
                    ...state,
                    filterByBrand: [action.payload],
                    // productWithFilter: [...newPro],
                    displayProducts: [...newPro],
                }
            }

            if (state.filterByBrand.find((el) => el === action.payload)) { 
                const newBrandsFilter = state.filterByBrand.filter(el => el !== action.payload)
                const newPro: Product[] = state.productWithFilter.filter(el => el.brand !== action.payload)
                if (newPro.length === 0) {
                    return {
                        ...state,
                        filterByBrand: [],
                        productWithFilter: [],
                        displayProducts: [...state.productWithoutFilter.splice(state.startDisplayInd, state.countDisplayProduct)]
                    }
                }
                return {
                    ...state,
                    filterByBrand: [...newBrandsFilter],
                    productWithFilter: [...newPro],
                    displayProducts: [...newPro.splice(state.startDisplayInd, state.countDisplayProduct)]
                }
            }

            if (state.filterByBrand.find((el) => el !== action.payload)) {
                const newPro: Product[] = state.getProducts.filter(el => el.brand === action.payload)
                const display = [...state.productWithFilter, ...newPro]

                return {
                    ...state,
                    filterByBrand: [...state.filterByBrand, action.payload],
                    productWithFilter: [...display],
                    displayProducts: [...display.splice(state.startDisplayInd, state.countDisplayProduct)]
                }
            }
            return {
                ...state,
            }

        case ProductsActionTypes.GO_TO_NEXT_PAGE:
            if (state.productWithFilter.length === 0) {
                return {
                    ...state,
                    productWithoutFilter: [...state.productWithoutFilter],
                    displayProducts: [...state.productWithoutFilter.splice(state.startDisplayInd + 6, state.countDisplayProduct)],
                    startDisplayInd: state.startDisplayInd + 6,
                    pageProduct: state.pageProduct + 1,
                }
            }
            if (state.productWithFilter.length > 0) {
                return {
                    ...state,
                    productWithFilter: [...state.productWithFilter],
                    displayProducts: [...state.productWithFilter.splice(state.startDisplayInd + 6, state.countDisplayProduct)],
                    startDisplayInd: state.startDisplayInd + 6,
                    pageProduct: state.pageProduct + 1,
                }
            }
            return { ...state }
        case ProductsActionTypes.GO_TO_PREVIOUS_PAGE:
            if (state.productWithFilter.length === 0) {
                return {
                    ...state,
                    productWithoutFilter: [...state.productWithoutFilter],
                    displayProducts: [...state.productWithoutFilter.splice(state.startDisplayInd - 6, state.countDisplayProduct)],
                    startDisplayInd: state.startDisplayInd - 6,
                    pageProduct: state.pageProduct - 1
                }
            }
            if (state.productWithFilter.length > 0) {
                return {
                    ...state,
                    productWithFilter: [...state.productWithFilter],
                    displayProducts: [...state.productWithFilter.splice(state.startDisplayInd - 6, state.countDisplayProduct)],
                    startDisplayInd: state.startDisplayInd - 6,
                    pageProduct: state.pageProduct - 1
                }
            }
            return { ...state }

        default:
            return {
                ...state,
            }
    }
}
