import React, { useEffect } from "react";
import ProductElement from "./ProductElement/ProductElement";
import './ProductContainer.css'
import { useTypedSelector } from "../../hooks/useTypedSelector";
import { Product } from "../../types/products";
import { useActions } from "../../hooks/useActions";

const ProductContainer = () => {
    const {allProducts, filterByBrand, displayProducts, pageProduct } = useTypedSelector(state => state.product)
    const {viewProducts} = useActions()

    console.log(displayProducts)


    const display:Product[] = []
     allProducts.map ( el => {
        filterByBrand.map( bra => {
            if( bra === el.brand ) {
                display.push(el) 
                displayProducts.push(el)
            }
        })
    })

    if (display.length > 0) return (
        <div className="ProductContainer">
            {display.map( el => <ProductElement key={el.id} product={el} />)}
            <button className="loadMoreButton" style={{alignContent: 'center'}}> load more items </button>
        </div>
    )

    return (
        <div className="ProductContainer">
            {displayProducts.map( el => <ProductElement key={el.id} product={el} />)}
            <div className="changePages">
                <button className="loadMoreButton" style={{alignContent: 'center', display: pageProduct === 1 ? 'none' : 'block'}}> Previous </button>
                <p> {pageProduct} </p>
                <button className="loadMoreButton" style={{alignContent: 'center'}}> Next page </button>
            </div>

        </div>
    )
}

export default ProductContainer
